// Define your initial state
const initialState = {
    counter: 0,
};

// Define your reducer function
const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'INCREMENT':
            return {
                ...state,
                counter: state.counter + 1,
            };
        case 'DECREMENT':
            return {
                ...state,
                counter: state.counter - 1,
            };
        default:
            return state;
    }
};

export default userReducer;
